var fs = require('fs');


var len = 0,
	s = 1,
	path,
	currentPath,
	pathList;

function make(path){
	// currentPath = undefined;
	pathList = path.split('/');
	console.log(pathList,"  pathList###############");
	len = pathList.length;
	return core(pathList[0] +'/'+ pathList[1]);
}
function core(path){
	// console.log(path," ~~~~~~~~~~~~~~**********************");
	if(s === len){
		s = 1;
		return currentPath;
	} 
	if(!fs.existsSync(path)){
		console.log(path+"  path -------------------")
		fs.mkdirSync(path);
	}
	currentPath = path;
	s++;
	return core(currentPath +'/'+ pathList[s]);
}

module.exports = make;