var os = require('os');

var systems = {
	init:function(req,res,next){
		var str = this.output();
		res.send(str);

	},
	output:function(){
		var properties = [
			{
				property:'hostname',
				description:'主机名：is: os.hostname---'
			},
			{
				property:'type',
				description:'操作系统名称：is: os.type---'
			},
			{
				property:'platform',
				description:'操作系统平台：is: os.platform---'
			},
			{
				property:'arch',
				description:'操作系统 CPU 架构: is: os.arch---'
			},
			{
				property:'release',
				description:'操作系统的发行版本: is: os.release----'
			},
			{
				property:'totalmem',
				description:'系统内存总量：is: os.totalmem---'
			}
		]
		var result = '';
		for(var s = 0,len = properties.length;s<len;s++){
			result += properties[s].description + os[properties[s].property]() +" </br>";
		}
		return result;
	}
}
module.exports = systems;